INSERT INTO `dashboard_sitecontent` VALUES (48,'About Us','<h2><small>Our goal is to facilitate fun, informative, inspirational and recreational outdoor adventure experiences.<br />\r\n<br />\r\nActivities include<br />\r\n<br />\r\n&bull; Trekking<br />\r\n<br />\r\n&bull; Climbing<br />\r\n<br />\r\n&bull; Hiking<br />\r\n<br />\r\n&bull; Bouldering<br />\r\n<br />\r\n&bull; Camping<br />\r\n<br />\r\n&bull; Adventure treks<br />\r\n<br />\r\n&bull; Sports<br />\r\n<br />\r\n&bull; Fitness<br />\r\n<br />\r\nOur state boasts of number of quality outdoor climbing locations all varying in location, scenery, rock quality and texture.<br />\r\n<br />\r\nClimbOn is registered under Government of Telangana<br />\r\n<br />\r\nWe plans regular and spontaneous climbing gym sessions, organizes outdoor climbing and camping adventures. Club provide unique opportunity of learning from our certified trainer the basics of rock climbing and improving your top-roping skills and techniques. Our members are climbers of all experience levels. Whether you are new to the sport and looking to learn, experienced and in need of new partners, or new to town and trying to find the climbing community, ClimbOn can be your gateway to adventure.<br />\r\n<br />\r\nRock climbing got numerous benefits - get fit, lose weight, become stronger, balance body and mind, enhance problem solving ability.</small></h2>','2018-11-17 05:13:17.694594',1,1,'None',1),(52,'contact Us','<p>ClimbOn Naveen at (+1) 343-988-3609 Canada<br />\r\n<br />\r\nClimbOn Sandeep at 8008004360<br />\r\n<br />\r\nClimbOn Rohit at 9676722155</p>','2018-11-19 05:19:02.267100',1,3,'None',1),(53,'hai','<p>2q12eqq2</p>','2018-11-20 05:29:23.571746',0,4,'None',1),(54,'abc','<p>1qxxxsdwdw</p>','2018-11-20 05:29:36.715586',0,2,NULL,1),(55,'ft tryftugjhkjlkm.,','<p>ssssssssssssssssswwwwwwwwwwwwwwwww</p>','2018-11-20 05:29:51.244815',0,5,NULL,1);
